class Juego {
    constructor(nombre, categoria, imagen) {
        this.nombre = nombre;
        this.categoria = categoria;
        this.imagen = imagen; // Nueva propiedad para la imagen
    }

    mostrarJuego() {
        const divJuego = document.createElement('div');
        divJuego.classList.add('juego');
        divJuego.setAttribute('data-categoria', this.categoria);
        divJuego.innerHTML = `
            <img src="${this.imagen}" alt="${this.nombre}" class="imagen-juego"> <!-- Agregando la imagen -->
            <h3>${this.nombre}</h3>
            <p>Categoría: ${this.categoria}</p>
            <button class="favorito">❤️ Agregar a Favoritos</button>
            <button class="jugar">Jugar</button>
        `;
        document.getElementById('lista-juegos').appendChild(divJuego);

        const favoritoBtn = divJuego.querySelector('.favorito');

        // Verifica si el juego ya es favorito y actualiza el botón
        if (this.esFavorito()) {
            favoritoBtn.classList.add('clicked');
        }

        favoritoBtn.addEventListener('click', (e) => {
            e.stopPropagation(); // Evita la propagación del evento al div
            this.toggleFavorito(divJuego);
        });

        divJuego.querySelector('.jugar').addEventListener('click', () => {
            cargarDetalleJuego(this.nombre); // Carga los detalles del juego
        });
    }

    toggleFavorito(juegoElemento) {
        const favoritoBtn = juegoElemento.querySelector('.favorito');
        favoritoBtn.classList.toggle('clicked');
        const nombreJuego = juegoElemento.querySelector('h3').textContent;

        if (favoritoBtn.classList.contains('clicked')) {
            this.agregarAFavoritos(nombreJuego);
        } else {
            this.removerDeFavoritos(nombreJuego);
        }
    }

    esFavorito() {
        const favoritos = JSON.parse(localStorage.getItem('favoritos')) || [];
        return favoritos.includes(this.nombre);
    }

    agregarAFavoritos(nombreJuego) {
        const favoritos = JSON.parse(localStorage.getItem('favoritos')) || [];
        if (!favoritos.includes(nombreJuego)) {
            favoritos.push(nombreJuego);
            localStorage.setItem('favoritos', JSON.stringify(favoritos));
        }
    }

    removerDeFavoritos(nombreJuego) {
        let favoritos = JSON.parse(localStorage.getItem('favoritos')) || [];
        favoritos = favoritos.filter(fav => fav !== nombreJuego);
        localStorage.setItem('favoritos', JSON.stringify(favoritos));
    }
}

// Lista de juegos con imágenes usando URLs
const juegos = [
    new Juego('Juego 1', 'free-to-play', 'data:image/jpeg;base64,'),
    new Juego('Juego 2', 'accion', 'data:image/jpeg;base64,'),
    new Juego('Juego 3', 'sport', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTpeIKX28Euq0LQp7Iavf9K9t61yTtdOaxx-Q&s'),
    new Juego('Juego 4', 'adventure', 'data:image/jpeg;base64,'),
    new Juego('Juego 5', 'free-to-play', 'data:image/jpeg;base64,'),
    new Juego('Juego 6', 'accion', 'data:image/jpeg;base64,')
];

function mostrarTodosLosJuegos() {
    juegos.forEach(juego => juego.mostrarJuego());
}

function filtrarJuegos(categoria) {
    const juegos = document.querySelectorAll('.juego');
    juegos.forEach(juego => {
        if (categoria === 'all' || juego.getAttribute('data-categoria') === categoria) {
            juego.style.display = 'block';
        } else {
            juego.style.display = 'none';
        }
    });
}

document.querySelectorAll('.filtro-btn').forEach(button => {
    button.addEventListener('click', () => {
        const categoria = button.getAttribute('data-categoria');
        filtrarJuegos(categoria);
    });
});

// Mostrar los juegos al cargar la página
mostrarTodosLosJuegos();

// Función para cargar los juegos favoritos en la página favoritos.html
function cargarJuegosFavoritos() {
    const favoritos = JSON.parse(localStorage.getItem('favoritos')) || [];

    favoritos.forEach(nombreJuego => {
        const divJuegoFavorito = document.createElement('div');
        divJuegoFavorito.classList.add('juego');
        divJuegoFavorito.innerHTML = `
            <h3>${nombreJuego}</h3>
            <button class="favorito clicked">❤️</button>
            <button class="jugar">Jugar</button>
        `;

        document.getElementById('lista-favoritos').appendChild(divJuegoFavorito);
    });
}

// Verificar si estamos en la página de favoritos y cargar los juegos
if (window.location.pathname.includes('favoritos.html')) {
    cargarJuegosFavoritos();
}

// Juego detallado
function cargarDetalleJuego(nombre) {
    const juego = juegos.find(j => j.nombre === nombre);
    if (juego) {
        window.location.href = 'juego-detalle.html'; // Redirigir a la página de detalle
        // Cargar los detalles del juego en esa página
        document.getElementById('nombre-juego').textContent = juego.nombre;
        document.getElementById('imagen-juego').src = juego.imagen; // Agregar imagen al detalle
        document.getElementById('descripcion-juego').textContent = `Descripción de ${juego.nombre}.`;
    }
}
